def Power(x,n):
	return x**n
def lam(a,b,c):
	return a**2 + b**2 + c**2 -2*a*b - 2*b*c - 2*a*c
def Sqrt(x):
	return np.sqrt(x)



class cascade(vg.BatchIntegrand):

	def __init__(self, dim, Emin, Emax, MC_case):
		self.dim = dim
		self.Emax = Emax
		self.Emin = Emin
		self.MC_case = MC_case
		self.flux = MC_case.flux
				
	def __call__(self, x):

		MA = self.MC_case.MA
		Z = self.MC_case.Z
		params = self.MC_case.params
		mzprime = params.mzprime
		if self.MC_case.nu_upscattered==pdg.neutrino4:
			Mn = params.m4
		elif self.MC_case.nu_upscattered==pdg.neutrino5:
			Mn = params.m5
		else:
			print("Could not specify upscattered neutrino mass.")
		m_ell = self.MC_case.m_ell
		alphaD = params.gD**2/4.0/np.pi
		Umu4 = params.Umu4
		epsilon = params.chi * const.cw
		UD4     = params.UD4

		Enu = (self.Emax - self.Emin) * x[:, 1] + self.Emin

		#######################
		# Upscattering N decay

		s = MA**2 + 2*Enu*MA

		E1CM = (s - MA**2)/2.0/np.sqrt(s)
		E3CM = (s + Mn**2 - MA**2)/2.0/np.sqrt(s)

		p1CM = E1CM # massless
		p3CM = np.sqrt(E3CM**2 - Mn**2)

		# Q2lmin = np.log(-(Mn**2 - 2 *  E1CM*(E3CM - p3CM) ))
		# Q2lmax = np.log(-(Mn**2 - 2 *  E1CM*(E3CM + p3CM)))
		Q2lmin = np.log(-(Mn*Mn- 2 * ( E1CM*E3CM - p1CM*p3CM) ))
		Q2lmax = np.log(-(Mn*Mn- 2 * ( E1CM*E3CM + p1CM*p3CM)))

		Q2l = (Q2lmax - Q2lmin) * x[:, 0] + Q2lmin
		Q2 = np.exp(Q2l)

		u = 2*MA**2 + Mn**2 - s + Q2

		# Start sigma
		dsigma = 1.0

		# BSM parameters
		dsigma *= Umu4**2*alphaD*4.0*np.pi/4.0
		dsigma *= epsilon**2

		# Z' propagator
		dsigma *= 1.0/(Q2 + mzprime**2)**2

		# Hadronic tensor coupling
		dsigma *= Power(const.eQED,2)

		F1_p = const.F1_EM(Q2)#F1_SM(Q2)
		F2_p = const.F2_EM(Q2)#F2_SM(Q2)
		F1_coh = const.FEMcoh(np.sqrt(Q2),MA)
		F2_coh = 0
		FA = 0

		dsigma_p = dsigma*16*Power(Enu,2)*(4*(Power(F1_p,2) + Power(FA,2))*Power(MA,2) + Power(F2_p,2)*Q2) + ((Power(Mn,2) + Q2)*(-16*(F1_p - FA)*(F1_p + FA)*Power(MA,4) - 4*F2_p*(2*F1_p + F2_p)*Power(MA,2)*Power(Mn,2) + 4*(2*Power(F1_p,2) + Power(F2_p,2) + 4*F2_p*FA + 2*Power(FA,2) + 4*F1_p*(F2_p + FA))*Power(MA,2)*Q2 + Power(F2_p,2)*Power(Mn,2)*Q2))/Power(MA,2) - (8*Enu*(8*F1_p*FA*Power(MA,2)*Q2 + 8*F2_p*FA*Power(MA,2)*Q2 + 4*Power(F1_p,2)*Power(MA,2)*(Power(Mn,2) + Q2) + 4*Power(FA,2)*Power(MA,2)*(Power(Mn,2) + Q2) + Power(F2_p,2)*Q2*(Power(Mn,2) + Q2)))/MA
		# dsigma *= 16*F2*FA*Q2*(2*Power(MA,2) + Power(Mn,2) + Q2 - 2*s) + 8*F1*(4*FA*Power(MA,2)*Q2 - (Power(Mn,2) + Q2)*(F2*Power(Mn,2) - 2*(F2 + FA)*Q2) - 4*FA*Q2*s) + 8*Power(FA,2)*(2*Power(MA,4) + Q2*(Power(Mn,2) + Q2) + 4*Power(MA,2)*(Power(Mn,2) + Q2 - s) - 2*(Power(Mn,2) + Q2)*s + 2*Power(s,2)) + 8*Power(F1,2)*(Q2*(Power(Mn,2) + Q2) + 2*(Power(MA,4) - (2*Power(MA,2) + Power(Mn,2) + Q2)*s + Power(s,2))) + (Power(F2,2)*(4*Power(MA,4)*Q2 - 4*Power(MA,2)*(Power(Mn,4) - Power(Mn,2)*Q2 + 2*Q2*(-Q2 + s)) + Q2*(Power(Mn,4) + Power(Mn,2)*(Q2 - 4*s) + 4*s*(-Q2 + s))))/Power(MA,2)
		if Z==1:
			dsigma_coh = 0
		elif Z>1:
			dsigma_coh = dsigma*Z*Z*4.0*Power(F1_coh,2)*(4*Power(MA,4) + Power(Mn,4) + Power(Mn,2)*Q2 - 4*(2*Power(MA,2) + Power(Mn,2) + Q2)*s + 4*Power(s,2))
		# dsigma *= Z*Z*4*Power(F1,2)*(4*Power(MA,4) + Power(Mn,4) + Power(Mn,2)*Q2 - 4*(2*Power(MA,2) + Power(Mn,2) + Q2)*s + 4*Power(s,2))

		betaPS = np.sqrt(1.0 - 2*(Mn**2 + MA**2)/s + (Mn**2 - MA**2)**2/s**2)

		dsigma_p *= betaPS/(32*np.pi**2)
		dsigma_p *= 1.0/(4.0*Enu*MA) 
		dsigma_p *= 1.0/2.0/p1CM/p3CM

		# Integrated over phi 
		dsigma_p *= 2*np.pi
		dsigma_p *= (Q2lmax - Q2lmin)*np.exp(Q2l)
		dsigma_p *= (self.Emax - self.Emin)

		dsigma_coh *= betaPS/(32*np.pi**2)
		dsigma_coh *= 1.0/(4.0*Enu*MA) 
		dsigma_coh *= 1.0/2.0/p1CM/p3CM

		# Integrated over phi 
		dsigma_coh *= 2*np.pi
		dsigma_coh *= (Q2lmax - Q2lmin)*np.exp(Q2l)
		dsigma_coh *= (self.Emax - self.Emin)

		#######################
		## Heavy N Decay
		cost = -1.0 + (2.0) * x[:, 2]
		dgammaN = 2*UD4**2*(params.Ue4**2 + params.Umu4**2 + params.Utau4**2)*alphaD*4*np.pi*(Mn**2 - 2*mzprime**2 + Mn**4/mzprime**2)/2.0/Mn/32.0/np.pi**2*(1.0-mzprime**2/Mn**2) 
		############# If Dirac, include more stuff! ###################
		dgammaN *= (1.0 - params.Dirac*(Mn**2 - 2*mzprime**2)/(Mn**2 + 2*mzprime**2)*cost) 
		################################
		dgammaN *= 2*np.pi
		dgammaN *= 2 # Jacobian


		#######################
		## Zprime decay 
		dgammaMediator = (const.alphaQED*(epsilon*epsilon)*np.sqrt(-4*(m_ell*m_ell) + mzprime*mzprime)*(5*(m_ell*m_ell) + 2*(mzprime*mzprime))*np.pi)/(24.*(mzprime*mzprime)*(np.pi*np.pi))
		dgammaMediator *= 2*np.pi*2 # integral over angles

		###########################
		return {
		'pel': dsigma_p*self.flux(Enu)*dgammaN*dgammaMediator*Z,
		'coh': dsigma_coh*self.flux(Enu)*dgammaN*dgammaMediator,
		'flux_avg_sigma_p' : dsigma_p*self.flux(Enu),
		'flux_avg_sigma_coh' : dsigma_coh*self.flux(Enu),
		'gammaN' : dgammaN,
		'gammaMediator' : dgammaMediator
		}


class threebody(vg.BatchIntegrand):

			def __init__(self, dim, Emin, Emax, MC_case):
				self.dim = dim
				self.Emax = Emax
				self.Emin = Emin
				self.MC_case = MC_case
				self.flux = MC_case.flux
				params = MC_case.params
				final_lepton1 = MC_case.final_lepton
				final_lepton2 = MC_case.final_lepton
				self.h_upscattered = MC_case.h_upscattered

				if (MC_case.final_lepton==pdg.tau):
					self.CC_mixing = MC_case.params.Utau4
				elif(MC_case.final_lepton==pdg.muon):
					self.CC_mixing = MC_case.params.Umu4
				elif(MC_case.final_lepton==pdg.electron):
					self.CC_mixing = MC_case.params.Ue4
				else:
					print("WARNING! Unable to set CC mixing parameter for decay. Assuming 0.")
					self.CC_mixing = 0

				self.NCflag=1
				if MC_case.nu_upscattered==pdg.neutrino6:
					self.mh = params.m6


					###########################33
					# THESE ENTER INTO SCATTERING
					self.cij=params.Umu6*const.gweak/2.0/const.cw
					self.cji=self.cij
					self.dij=params.UD6*params.Umu6*params.gD
					self.dji=self.dij

					# Which outgoing neutrino?
					if MC_case.nu_outgoing==pdg.nue:
						self.Cih = params.ce6/2.0
						self.Dih = params.de6/2.0
						self.mf = 0.0
					elif MC_case.nu_outgoing==pdg.numu:
						self.Cih = params.cmu6/2.0
						self.Dih = params.dmu6/2.0
						self.mf = 0.0
					elif MC_case.nu_outgoing==pdg.nutau:
						self.Cih = params.ctau6/2.0
						self.Dih = params.dtau6/2.0
						self.mf = 0.0
					elif MC_case.nu_outgoing==const.nulight:
						self.Cih = params.clight6/2.0
						self.Dih = params.dlight/2.0
						self.mf = 0.0
					elif MC_case.nu_outgoing==pdg.neutrino4:
						self.Cih = params.c46/2.0
						self.Dih = params.d46/2.0
						self.mf = params.m4
					elif MC_case.nu_outgoing==pdg.neutrino5:
						self.Cih = params.c56/2.0
						self.Dih = params.d56/2.0
						self.mf = params.m5
				
				elif MC_case.nu_upscattered==pdg.neutrino5:
					self.mh = params.m5

					###########################33
					# THESE ENTER INTO SCATTERING
					self.cij=params.Umu5*const.gweak/2.0/const.cw
					self.cji=self.cij
					self.dij=params.UD5*params.Umu5*params.gD
					self.dji=self.dij

					# Which outgoing neutrino?
					if MC_case.nu_outgoing==pdg.nue:
						self.Cih = params.ce5/2.0
						self.Dih = params.de5/2.0
						self.mf = 0.0
					elif MC_case.nu_outgoing==pdg.numu:
						self.Cih = params.cmu5/2.0
						self.Dih = params.dmu5/2.0
						self.mf = 0.0
					elif MC_case.nu_outgoing==pdg.nutau:
						self.Cih = params.ctau5/2.0
						self.Dih = params.dtau5/2.0
						self.mf = 0.0
					elif MC_case.nu_outgoing==const.nulight:
						self.Cih = params.clight5/2.0
						self.Dih = params.dlight5/2.0
						self.mf = 0.0
					elif MC_case.nu_outgoing==pdg.neutrino4:
						self.Cih = params.c45/2.0
						self.Dih = params.d45/2.0
						self.mf = params.m4

				elif MC_case.nu_upscattered==pdg.neutrino4:
					self.mh = params.m4

					###########################33
					# THESE ENTER INTO SCATTERING
					self.cij=params.Umu4*const.gweak/2.0/const.cw
					self.cji=self.cij
					self.dij=params.UD4*params.Umu4*params.gD
					self.dji=self.dij


					# Which outgoing neutrino?
					if MC_case.nu_outgoing==pdg.nue:
						self.Cih = params.ce4/2.0
						self.Dih = params.de4/2.0
						self.mf = 0.0
					elif MC_case.nu_outgoing==pdg.numu:
						self.Cih = params.cmu4/2.0
						self.Dih = params.dmu4/2.0
						self.mf = 0.0
					elif MC_case.nu_outgoing==pdg.nutau:
						self.Cih = params.ctau4/2.0
						self.Dih = params.dtau4/2.0
						self.mf = 0.0
					elif MC_case.nu_outgoing==const.nulight:
						self.Cih = params.clight4/2.0
						self.Dih = params.dlight4/2.0
						self.mf = 0.0
					elif MC_case.nu_outgoing==pdg.neutrino4:
						print('ERROR! (nu4 -> nu4 l l) is kinematically not allowed!')
				


				################################################
				# DECIDE ON CHARGED LEPTON
				if MC_case.nu_outgoing/10==final_lepton1:
					# Mixing required for CC N-like
					if (final_lepton1==pdg.tau):
						self.CC_mixing1 = params.Utau4
					elif(final_lepton1==pdg.muon):
						self.CC_mixing1 = params.Umu4
					elif(final_lepton1==pdg.electron):
						self.CC_mixing1 = params.Ue4
					else:
						print("WARNING! Unable to set CC mixing parameter for decay. Assuming 0.")
						self.CC_mixing1 = 0

					# Mixing required for CC Nbar-like
					if (final_lepton2==pdg.tau):
						self.CC_mixing2 = params.Utau4
					elif(final_lepton2==pdg.muon):
						self.CC_mixing2 = params.Umu4
					elif(final_lepton2==pdg.electron):
						self.CC_mixing2 = params.Ue4
					else:
						print("WARNING! Unable to set CC mixing parameter for decay. Assuming 0.")
						self.CC_mixing2 = 0
				else:
					self.CC_mixing1 = 0
					self.CC_mixing2 = 0
				
				#######################################
				### WATCH OUT FOR THE MINUS SIGN HERE -- IMPORTANT FOR INTERFERENCE
				## Put required mixings in CCflags
				self.CCflag1 = self.CC_mixing1
				self.CCflag2 = -self.CC_mixing2

				##############################
				# CHARGED LEPTON MASSES 

				if (final_lepton1==pdg.tau):
					self.mm = const.m_tau
				elif(final_lepton1==pdg.muon):
					self.mm = const.m_mu
				elif(final_lepton1==pdg.electron):
					self.mm = const.m_e
				else:
					print("WARNING! Unable to set charged lepton mass. Assuming massless.")
					self.mm = 0

				if (final_lepton2==pdg.tau):
					self.mp = const.m_tau
				elif(final_lepton2==pdg.muon):
					self.mp = const.m_mu
				elif(final_lepton2==pdg.electron):
					self.mp = const.m_e
				else:
					print("WARNING! Unable to set charged lepton mass. Assuming massless.")
					self.mp = 0


