def cascade_phase_space(samples=None, MC_case=None, w=None, I=None):

	Q2p = samples[0]
	Enup =  samples[1]
	costZ = samples[2]
	sample_size = np.shape(Enup)[0]

	MA = MC_case.MA
	mh = MC_case.Mn
	Z = MC_case.Z
	params = MC_case.params
	mzprime = params.mzprime
	if MC_case.nu_upscattered==pdg.neutrino4:
		Mn = params.m4
	elif MC_case.nu_upscattered==pdg.neutrino5:
		Mn = params.m5
	else:
		print("Could not specify upscattered neutrino mass.")

	Mn = params.m4
	MA = MC_case.MA
	m_ell = MC_case.m_ell
	
	costZ = costZ*2.0 - 1.0

	######################
	# SCATTERING
	Enu = Enup*(MC_case.EMAX - MC_case.EMIN) + MC_case.EMIN

	# print Enup
	s = MA**2 + 2*Enu*MA

	E1CM = (s - MA**2)/2.0/np.sqrt(s)
	E2CM = (s + MA**2)/2.0/np.sqrt(s)
	E3CM = (s + mh**2 - MA**2)/2.0/np.sqrt(s)
	E4CM = (s - mh**2 + MA**2)/2.0/np.sqrt(s)

	p1CM = E1CM
	p2CM = np.sqrt(E2CM**2 - MA**2)
	p3CM = np.sqrt(E3CM**2 - mh**2)
	p4CM = np.sqrt(E4CM**2 - MA**2)

	Q2lmin = np.log(-(mh**2 - 2 * ( E1CM*E3CM - p1CM*p3CM) ))
	Q2lmax = np.log(-(mh**2 - 2 * ( E1CM*E3CM + p1CM*p3CM) ))

	Q2l = (Q2lmax - Q2lmin) * Q2p + Q2lmin
	Q2 = np.exp(Q2l)
	
	# KINEMATICS TO LAB FRAME
	costN = ( -Q2 - mh*mh + 2*E1CM*E3CM) / (2*p1CM*p3CM)
	beta = -p2CM/E2CM # ATTENTION TO MINUS SIGNS HERE
	gamma = 1.0/np.sqrt(1.0 - beta*beta)

	phiN = Cfv.random_generator(sample_size, 0.0, 2*np.pi)
	# 
	P1CM = Cfv.build_fourvec(E4CM, p1CM, np.ones(sample_size), phiN*0)
	P2CM = Cfv.build_fourvec(E4CM, -p1CM, np.ones(sample_size), phiN*0)
	P3CM = Cfv.build_fourvec(E3CM, p3CM, costN, phiN)
	P4CM = Cfv.build_fourvec(E4CM, -p3CM, costN, phiN)

	# incoming neutrino
	P1LAB = Cfv.L(P1CM, beta)
	# incoming Hadron		
	P2LAB = Cfv.L(P2CM, beta)
	# outgoing neutrino
	P3LAB = Cfv.L(P3CM, beta)
	# outgoing Hadron
	P4LAB = Cfv.L(P4CM, beta)

	EN_LAB = P3LAB.T[0]
	costN_LAB =  P3LAB.T[3]/np.sqrt(P3LAB.T[1]**2 + P3LAB.T[2]**2 + P3LAB.T[3]**2)
	phiN_LAB = np.arctan2(P3LAB.T[2], P3LAB.T[1])


	######################
	# Decay N

	E1CM_decay = np.ones((np.shape(costZ)[0])) * (Mn**2 - mzprime**2) /2.0/Mn
	p1CM_decay = np.ones((np.shape(costZ)[0])) * E1CM_decay
	E2CM_decay = np.ones((np.shape(costZ)[0])) * (Mn**2 + mzprime**2) /2.0/Mn
	p2CM_decay = np.ones((np.shape(costZ)[0])) * np.sqrt(E2CM_decay**2 - mzprime**2)

	# phiz = [ random.random()*2.0*np.pi for i in range(0,np.size(costZ)) ]
	phiz   = Cfv.random_generator(sample_size, 0.0, 2*np.pi)

	# P2CM_decay = [ [E2CM_decay,
	# 					  		p2CM_decay*np.cos(phiz)*np.sin(thetaz), 
	# 					  		p2CM_decay*np.sin(phiz)*np.sin(thetaz), 
	# 					  		p2CM_decay*np.cos(thetaz)] for i in range(0,np.size(thetaz))]

	P2CM_decay = Cfv.build_fourvec(E2CM_decay, -p2CM_decay, costZ, phiz)


	### Transform P2_CM into the LAB frame (determined by the PN vector)
	P2LAB_decay = Cfv.Tinv(
								P2CM_decay, 
								-np.sqrt(EN_LAB**2 - Mn**2) / EN_LAB, 
								costN_LAB,  
								phiN_LAB)
	
	P1LAB_decay = P3LAB-P2LAB_decay
	

	EZ_LAB = P2LAB_decay.T[0]
	costZ_LAB =  P2LAB_decay.T[3]/np.sqrt(P2LAB_decay.T[1]**2 + P2LAB_decay.T[2]**2 + P2LAB_decay.T[3]**2)
	phiZ_LAB = np.arctan2(P2LAB_decay.T[2], P2LAB_decay.T[1])

	######################
	# Decay Z prime

	E1CM_decayZ = np.ones((np.shape(costZ)[0])) * mzprime/2.0 
	p1CM_decayZ = np.ones((np.shape(costZ)[0])) * np.sqrt(E1CM_decayZ**2 - m_ell**2) 

	cthetae = Cfv.random_generator(sample_size, -1, 1)
	phie   = Cfv.random_generator(sample_size, 0.0, 2*np.pi)


	P1CM_decayZ = Cfv.build_fourvec(E1CM_decayZ, p1CM_decayZ, cthetae, phie)
	P2CM_decayZ = Cfv.build_fourvec(E1CM_decayZ, -p1CM_decayZ, cthetae, phie)

	### Transform into the LAB frame (determined by the PZ_LAB vector)
	P1LAB_decayZ = Cfv.Tinv(
							P1CM_decayZ, 
							-np.sqrt( EZ_LAB**2 - mzprime**2)/EZ_LAB, 
							costZ_LAB,  
							phiZ_LAB)

	P2LAB_decayZ = Cfv.Tinv(
							P2CM_decayZ, 
							-np.sqrt( EZ_LAB**2 - mzprime**2)/EZ_LAB, 
							costZ_LAB,  
							phiZ_LAB)

	##########################################################################

	# return P3LAB, P2LAB_decay, P1LAB_decayZ, P2LAB_decayZ, P1LAB_decay, P4LAB
	# returing dictionary
	# return  {"P1" : P1LAB,
	# 		"P2" : P2LAB,
	# 		"P3" : P3LAB,
	# 		"P4" : P4LAB,
	# 		"P1_decay" : P1LAB_decay, 
	# 		"P2_decay" : P2LAB_decay, 
	# 		"P3_decay" : P1LAB_decayZ, 
	# 		"P4_decay" : P2LAB_decayZ,
	# 		"w" : w,
	# 		"I" : I}
	# returing dictionary
	return  {"P_scatterer" : P1LAB, 
			"P_target" : P2LAB, 
			"P_outgoing_HNL" : P3LAB, 
			"P_outgoing_target" : P4LAB, 
			"P_HNL" : P1LAB_decay, 
			"P_mediator" : P2LAB_decay, 
			"P_out_nu" : P1LAB_decay, 
			"P_em" : P1LAB_decayZ, 
			"P_ep" : P2LAB_decayZ,
			"w" : w,
			"I" : I

			}

def three_body_phase_space(samples=None, MC_case=None, w=None, I=None):

		Q2p = np.array(samples[0])
		Enup =  np.array(samples[1])
		vp = np.array(samples[2])
		up = np.array(samples[3])
		c3p = np.array(samples[4])
		phi34p = np.array(samples[5])
	
		sample_size = np.shape(Enup)[0]

		mh = MC_case.Mn
		MA = MC_case.MA
		m_ell = MC_case.m_ell

		######################
		# SCATTERING
		Enu = Enup*(MC_case.EMAX - MC_case.EMIN) + MC_case.EMIN

		# print Enup
		s = MA**2 + 2*Enu*MA

		E1CM = (s - MA**2)/2.0/np.sqrt(s)
		E2CM = (s + MA**2)/2.0/np.sqrt(s)
		E3CM = (s + mh**2 - MA**2)/2.0/np.sqrt(s)
		E4CM = (s - mh**2 + MA**2)/2.0/np.sqrt(s)

		p1CM = E1CM
		p2CM = np.sqrt(E2CM**2 - MA**2)
		p3CM = np.sqrt(E3CM**2 - mh**2)
		p4CM = np.sqrt(E4CM**2 - MA**2)

		Q2lmin = np.log(-(mh**2 - 2 * ( E1CM*E3CM - p1CM*p3CM) ))
		Q2lmax = np.log(-(mh**2 - 2 * ( E1CM*E3CM + p1CM*p3CM) ))

		Q2l = (Q2lmax - Q2lmin) * Q2p + Q2lmin
		Q2 = np.exp(Q2l)
		
		# KINEMATICS TO LAB FRAME
		costN = ( -Q2 - mh*mh + 2*E1CM*E3CM) / (2*p1CM*p3CM)
		beta = -p2CM/E2CM # ATTENTION TO MINUS SIGNS HERE
		gamma = 1.0/np.sqrt(1.0 - beta*beta)



		phiN = Cfv.random_generator(sample_size, 0.0, 2*np.pi)
		# 
		P1CM = Cfv.build_fourvec(E4CM, p1CM, np.ones(sample_size), phiN*0)
		P2CM = Cfv.build_fourvec(E4CM,-p1CM, np.ones(sample_size), phiN*0)
		P3CM = Cfv.build_fourvec(E3CM, p3CM, costN, phiN)
		P4CM = Cfv.build_fourvec(E4CM,-p3CM, costN, phiN)

		# incoming neutrino
		P1LAB = Cfv.L(P1CM, beta)
		# incoming Hadron		
		P2LAB = Cfv.L(P2CM, beta)
		# outgoing neutrino
		P3LAB = Cfv.L(P3CM, beta)
		# outgoing Hadron
		P4LAB = Cfv.L(P4CM, beta)


		######################################
		# Decay

		# boost parameters
		EN_LAB = P3LAB.T[0]
		costN_LAB =  P3LAB.T[3]/np.sqrt(P3LAB.T[1]**2 + P3LAB.T[2]**2 + P3LAB.T[3]**2)
		phiN_LAB = np.arctan2(P3LAB.T[2], P3LAB.T[1])

		mf = MC_case.Mn_outgoing 
		mm = MC_case.m_ell 
		mp = MC_case.m_ell 
		m_ell = MC_case.m_ell 

		uplus = (mh-mm)*(mh-mm)
		uminus = (mf+mp)*(mf+mp)			
		u = (uplus - uminus)*up + uminus
		vplus = mf*mf + mm*mm - ((-(mh*mh) + mm*mm + u)*(mf*mf - mp*mp + u))/(2.*u) + 2*Sqrt(-(mm*mm) + ((-(mh*mh) + mm*mm + u)*(-(mh*mh) + mm*mm + u))/(4.*u))*Sqrt(-(mf*mf) + ((mf*mf - mp*mp + u)*(mf*mf - mp*mp + u))/(4.*u))
		vminus = mf*mf + mm*mm - ((-(mh*mh) + mm*mm + u)*(mf*mf - mp*mp + u))/(2.*u) - 2*Sqrt(-(mm*mm) + ((-(mh*mh) + mm*mm + u)*(-(mh*mh) + mm*mm + u))/(4.*u))*Sqrt(-(mf*mf) + ((mf*mf - mp*mp + u)*(mf*mf - mp*mp + u))/(4.*u))	
		v = (vplus - vminus)*vp + vminus
		t = mh*mh+mf*mf+mm*mm+mp*mp-u-v

		E3CM_decay = (mh*mh+mm*mm-u)/2.0/mh
		p3CM_decay = np.sqrt(E3CM_decay*E3CM_decay - mm*mm)
		E4CM_decay = (mh*mh+mp*mp-v)/2.0/mh
		p4CM_decay = np.sqrt(E4CM_decay*E4CM_decay - mp*mp)
		E2CM_decay = (mh*mh+mf*mf-t)/2.0/mh
		p2CM_decay = np.sqrt(E2CM_decay*E2CM_decay - mf*mf)

		# Angles of P_minus (P_3)
		c_theta3 = c3p*2-1.0
		phi3 = Cfv.random_generator(sample_size, 0.0, 2*np.pi)

		# Azimuthal angle of P_plus wrt to P_minus (phi_34)
		phi34 = phi34p*2.0*np.pi

		# polar angle is known function of u and v
		c_theta34 = (mm*mm+mp*mp-t+2*E3CM_decay*E4CM_decay)/(2.0*p3CM_decay*p4CM_decay)

    	# p1
		P1CM_decay = Cfv.build_fourvec(mh*np.ones(sample_size), np.zeros(sample_size), np.ones(sample_size), np.ones(sample_size))
		# p3
		P3CM_decay = Cfv.build_fourvec(E3CM_decay, p3CM_decay, c_theta3, phi3)
		# p4 -- first in p3 along Z, then rotated to CM frame
		P4CM_decay = Cfv.rotationz(Cfv.rotationy_cos(Cfv.build_fourvec(E4CM_decay, p4CM_decay, c_theta34, phi34), c_theta3, sign=-1), phi3)
		# p2
		P2CM_decay = P1CM_decay - P4CM_decay - P3CM_decay 


		### Transform from CM into the LAB frame
		# Decaying neutrino
		P1LAB_decay = Cfv.Tinv(	P1CM_decay,
								-np.sqrt(EN_LAB**2 - mh**2)/EN_LAB, 
								costN_LAB,  
								phiN_LAB)
		# Outgoing neutrino
		P2LAB_decay = Cfv.Tinv(	P2CM_decay,
								-np.sqrt(EN_LAB**2 - mh**2)/EN_LAB, 
								costN_LAB,  
								phiN_LAB)
		# Outgoing lepton minus (3)
		P3LAB_decay = Cfv.Tinv(	P3CM_decay,
								-np.sqrt(EN_LAB**2 - mh**2)/EN_LAB, 
								costN_LAB,  
								phiN_LAB)
		# Outgoing lepton plus (4)
		P4LAB_decay = Cfv.Tinv(	P4CM_decay,
								-np.sqrt(EN_LAB**2 - mh**2 )/ EN_LAB, 
								costN_LAB,  
								phiN_LAB)	

		# returing dictionary
		return  {"P_scatterer" : P1LAB,
				"P_target" : P2LAB,
				"P_outgoing_HNL" : P3LAB,
				"P_outgoing_target" : P4LAB,
				"P_HNL" : P1LAB_decay, 
				"P_out_nu" : P2LAB_decay, 
				"P_em" : P3LAB_decay, 
				"P_ep" : P4LAB_decay,
				"w" : w,
				"I" : I}
	